import Carousel from 'react-multi-carousel';
import 'react-multi-carousel/lib/styles.css';
import React, { useState, useEffect } from "react";

const testimonial = () => {
    const responsive = {
        superLargeDesktop: {
            // the naming can be any, depends on you.
            breakpoint: { max: 4000, min: 3000 },
            items: 5
        },
        desktop: {
            breakpoint: { max: 3000, min: 1024 },
            items: 3
        },
        tablet: {
            breakpoint: { max: 1024, min: 464 },
            items: 2
        },
        mobile: {
            breakpoint: { max: 464, min: 0 },
            items: 1
        }
    };

    const testimonials = [
        {
            name: "John Doe",
            testimonial: "I had a great shopping experience with Lapi-cart. The products were of high quality and the prices were very affordable.",
            image: "/images/testimonial1.jpg"
        },
        {
            name: "Jane Smith",
            testimonial: "I am very impressed with the customer service at Lapi-cart. They were very helpful and resolved my issues quickly.",
            image: "/images/testimonial2.jpg"
        },
        // add more testimonials as needed
    ];

    return (
        <div className="slider-aboutus">
            <Carousel responsive={responsive}>
                {testimonials.map((testimonial, index) => (
                    <div className="testimonial" key={index}>
                        <img src={testimonial.image} alt={testimonial.name} className="testimonial-image" />
                        <div className="testimonial-name">{testimonial.name}</div>
                        <div className="testimonial-text">{testimonial.testimonial}</div>
                    </div>
                ))}
            </Carousel>
        </div>

    );
};

export default testimonial;
