import React, { useState } from 'react';
import './Header.css';

const Header = () => {
    const [currentImage, setCurrentImage] = useState(0);
    const images = [
        '/images/image1.jpg',
        '/images/image2.jpg',
        '/images/image3.jpg',
        '/images/image4.jpg',
        '/images/image5.jpg'
    ];

    const prevImage = () => {
        if (currentImage === 0) {
            setCurrentImage(images.length - 3);
        } else {
            setCurrentImage(currentImage - 1);
        }
    };

    const nextImage = () => {
        if (currentImage === images.length - 3) {
            setCurrentImage(0);
        } else {
            setCurrentImage(currentImage + 1);
        }
    };

    return (
        <div className="carousel-container">
            <img className="carousel-image" src={images[currentImage]} alt="Carousel Image" />
            <div className="carousel-controls">
                <button onClick={prevImage}>Prev</button>
                <button onClick={nextImage}>Next</button>
            </div>
        </div>
    );
};

export default Header;
