import { useState, useEffect } from "react";
import "./Product.css"; // import CSS file

function Product() {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        fetch("/products.json")
            .then((response) => response.json())
            .then((data) => setProducts(data))
            .catch((error) => console.error(error));
    }, []);

    console.log("products", products);
    return (
        <div className="container">
            <div className="items">
            {products.map((product) => (
                <div className="product" key={product.id}> {/* Add a class name to each product */}
                    <div className="img img1"><img src={product.image} alt={product.name} /> </div>
                    <div className="name"><h3>{product.name}</h3></div>

                    <p>{product.description}</p>
                    <p>${product.price}</p>
                </div>
            ))}
        </div>
        </div>
    );
}

export default Product;
