import { useState, useEffect } from "react";
import "./Product.css"; // import CSS file

function Product() {
    const [products, setProducts] = useState([]);
    const [cartItems, setCartItems] = useState([]);


    useEffect(() => {
        fetch("/products.json")
            .then((response) => response.json())
            .then((data) => setProducts(data))
            .catch((error) => console.error(error));
    }, []);

    const handleAddToCart = (product) => {
        const newCartItems = [...cartItems];
        const index = newCartItems.findIndex((item) => item.id === product.id);
        if (index >= 0) {
            newCartItems[index].quantity += 1;
        } else {
            newCartItems.push({ ...product, quantity: 1 });
        }
        setCartItems(newCartItems);
    };


    console.log("products", products);
    return (
        <div className="product-list"> {/* Add a class name to the container */}
            {products.map((product) => (
                <div className="product" key={product.id}> {/* Add a class name to each product */}
                    <h3>{product.name}</h3>
                    <img id="img" src={product.image} alt={product.name} />
                    <p>{product.description}</p>
                    <p>{product.price} Rs</p>
                    <button onClick={() => handleAddToCart(product)}>Add to Cart</button>

                </div>
            ))}

        </div>


    );
}

export default Product;
