import React from 'react';
import './AboutUs.css'; // import your CSS file here

const AboutUs = () => {
    return (
        <div className="about-us">
            <h1>About Us</h1>
            <img src="images/ales-nesetril-Im7lZjxeLhg-unsplash.jpg" alt="About Us" style={{ width: '500px' }} className="animated-image" />
            <h2>Our Story</h2>
            <p>Lapikart is a leading ecommerce website that has been providing quality products and services for the last 10 years. Our mission is to make online shopping easy, affordable, and enjoyable for everyone.</p>
            <h3>Our Vision</h3>
            <p>Our vision is to be the go-to destination for all your shopping needs. We aim to provide a wide range of high-quality products at competitive prices, while also delivering exceptional customer service.</p>

        </div>
    );
}

export default AboutUs;
