import { Link } from "react-router-dom";

function Cart({ cartItems }) {
    return (
        <div className="cart">
            <h2>Cart</h2>
            <ul>
                {cartItems.map((item) => (
                    <li key={item.id}>
                        {item.name} x {item.quantity} - ${item.price * item.quantity}
                    </li>
                ))}
            </ul>
            <Link to="/">Continue Shopping</Link>
        </div>
    );
}

export default Cart;
