import React from 'react';
import './AboutUs.css'; // import your CSS file here

const AboutUs = () => {
    return (
        <div className="about-us">
            <h2 className="title">Our Story</h2>
            <p className="description">Lapikart is a leading ecommerce website that has been providing quality products and services for the last 10 years. Our mission is to make online shopping easy, affordable, and enjoyable for everyone.</p>
            <h3 className="subtitle">Our Vision</h3>
            <p className="description">Our vision is to be the go-to destination for all your shopping needs. We aim to provide a wide range of high-quality products at competitive prices, while also delivering exceptional customer service.</p>
            <img src="/images/andras-vas-Bd7gNnWJBkU-unsplash.jpg" alt="About Us" className="animated-image" style={{width: "50%"}} />
        </div>
    );
}

export default AboutUs;
