import React from 'react';
import Footer from "../components/Footer";
import "./Css/AboutUs.css";
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";

const AboutUs = () => {

    const responsive = {
        superLargeDesktop: {
            // the naming can be any, depends on you.
            breakpoint: { max: 4000, min: 3000 },
            items: 5
        },
        desktop: {
            breakpoint: { max: 3000, min: 1024 },
            items: 3
        },
        tablet: {
            breakpoint: { max: 1024, min: 464 },
            items: 2
        },
        mobile: {
            breakpoint: { max: 464, min: 0 },
            items: 1
        }
    };
    return (
        <div className="about-us">
            <div className="header-image-container">
                <img src="/images/aboutus.jpg" alt="About Us" className="header-image" />
                <div className="contact-text">About Us</div>
            </div>

            <div className="content-container">
                <h2>Our Story</h2>
                <p>Lapikare look forward to serving you and providing you with the best possible shopping experience.
                </p>
                <h3>Our Vision</h3>
                <p>At Lapikart, our vision is to be the leading online marketplace for customers worldwide. We believe that everyone should have access to high-quality products at affordable prices, and we are committed to making this vision a reality.

                    for all your
                </p>
            </div>

            <div className="slider-aboutus">
                <Carousel responsive={responsive}>
                    <div>Item 1</div>
                    <div>Item 2</div>
                    <div>Item 3</div>
                    <div>Item 4</div>
                </Carousel>
            </div>
        </div>
    );
}

export default AboutUs;
