import { useState, useEffect } from "react";
import "./Product.css"; // import CSS file

function Product() {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        fetch("/products.json")
            .then((response) => response.json())
            .then((data) => setProducts(data))
            .catch((error) => console.error(error));
    }, []);

    console.log("products", products);
    return (
        <div className="product">

            {products.map((product) => (
                <div className="product">
                <div className="container">
                    <div className="items">
                <div className="product" key={product.id}> {/* Add a class name to each product */}
                    <div className="img img1"><img src={product.image} alt={product.name} /> </div>
                    <div className="name"><h3>{product.name}</h3></div>

                    <div class="info"> <p>{product.description}</p></div>
                    <div className="price"><p>${product.price}</p></div>
                </div>
                </div>
                </div>
                </div>
            ))}
        </div>

    );
}

export default Product;
